package com.headissue.service;

import com.headissue.domain.AccessRule;
import com.headissue.domain.UtmParameters;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.PrintWriter;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.Base64;
import org.yaml.snakeyaml.Yaml;

public class TestDataService {
  private final File directory;
  private final Yaml yaml;
  public static String defaultAccess = "00000000";
  public static String downloadable = "00000001";
  public static String expired = "00000002";

  public TestDataService(File directory, Yaml yaml) {
    this.directory = directory;
    this.yaml = yaml;
  }

  public void writeStaticTestFiles() {
    Path pdfPath = Paths.get(directory.getPath(), "test.pdf");

    createAccessRule(
        defaultAccess,
        new AccessRule(
            "test.pdf",
            365 * 100,
            new UtmParameters(null, null, "test", null, null),
            "yours truly",
            false));
    createAccessRule(
        downloadable,
        new AccessRule(
            "test.pdf",
            365 * 100,
            new UtmParameters(null, null, "test", null, null),
            "yours truly",
            true));
    createAccessRule(
        expired,
        new AccessRule(
            "test.pdf",
            -1,
            new UtmParameters(null, null, "test", null, null),
            "yours truly",
            true));
    try (FileOutputStream fos = new FileOutputStream(pdfPath.toFile())) {
      String samplePdf =
          "";
      byte[] decoder = Base64.getDecoder().decode(samplePdf);
      fos.write(decoder);
    } catch (Exception e) {
      e.printStackTrace();
    }
  }

  private void createAccessRule(String yamlName, AccessRule accessRule) {
    Path yamlPath = Paths.get(directory.getPath(), yamlName + ".yaml");
    try (PrintWriter p = new PrintWriter(new FileOutputStream(yamlPath.toFile()))) {
      yaml.dump(accessRule, p);
    } catch (IOException e) {
      throw new RuntimeException(e);
    }
  }
}
